mapper.select:
SELECT
    COUNT(CONTAINER_ID)
FROM
    TB_CONTAINER

    CONTAINER_OWNER_ID = #{containerOwnerId}

        AND CONTAINER_NAME LIKE CONCAT ('%', #{searchName}, '%')


        AND CONTAINER_STATUS = #{searchName}


        AND CONTAINER_APPROVAL_STATUS = #{searchName}

SELECT ROW_NUMBER() OVER (ORDER BY C.CONTAINER_CREATED ASC) AS ROW_NUM, C.CONTAINER_ID, C.CONTAINER_NAME, C.CONTAINER_SIZE, C.CONTAINER_STATUS, C.CONTAINER_APPROVAL_STATUS, C.CONTAINER_CREATED, O.OWNER_ID, O.OWNER_NM FROM TB_CONTAINER C INNER JOIN TB_OWNER O ON C.CONTAINER_OWNER_ID = O.OWNER_ID
CONTAINER_OWNER_ID = #{containerOwnerId}
AND CONTAINER_NAME LIKE CONCAT ('%', #{searchName}, '%')
AND CONTAINER_STATUS = #{searchName}
AND CONTAINER_APPROVAL_STATUS = #{searchName}
ORDER BY CONTAINER_CREATED DESC LIMIT ${startRowNum}, ${pageSize}
