mapper.select:
SELECT COUNT(P.PAYMENT_ID) FROM TB_PAYMENT P INNER JOIN TB_RESERVATION R ON P.PAYMENT_RESERVATION_ID = R.RESERVATION_ID INNER JOIN TB_CONTAINER C ON R.RESERVATION_CONTAINER_ID = C.CONTAINER_ID
P.PAYMENT_USER_ID = #{paymentUserId}
AND P.PAYMENT_STATUS = #{searchName}
AND C.CONTAINER_NAME LIKE CONCAT('%', TRIM(#{searchName}), '%')

SELECT ROW_NUMBER() OVER (ORDER BY P.PAYMENT_APPROVED ASC) AS ROW_NUM, P.PAYMENT_ID, P.PAYMENT_AMOUNT, P.PAYMENT_STATUS, P.PAYMENT_APPROVED, P.PAYMENT_CANCELED, C.CONTAINER_NAME FROM TB_PAYMENT P INNER JOIN TB_RESERVATION R ON P.PAYMENT_RESERVATION_ID = R.RESERVATION_ID INNER JOIN TB_CONTAINER C ON R.RESERVATION_CONTAINER_ID = C.CONTAINER_ID
P.PAYMENT_USER_ID = #{paymentUserId}
AND P.PAYMENT_STATUS = #{searchName}
AND C.CONTAINER_NAME LIKE CONCAT('%', TRIM(#{searchName}), '%')
ORDER BY P.PAYMENT_APPROVED DESC LIMIT #{startRowNum}, #{pageSize}
